methods.method:
function Constructor(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				oCurObj.setAttribute("highlighted","0");
				oCurObj.setAttribute("active","0");
				if(!g_isMSIE)
				{
					var oHighlighted = document.getElementById(argobj.pid + "_HIGHLIGHT");
					oHighlighted.style.width = oHighlighted.getAttribute("ww3c");
					oHighlighted.style.height = oHighlighted.getAttribute("hw3c");
				}
				return true;
			}

function HighlightOn(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.style.display=="none" || oCurObj.getAttribute("active")=="1" || oCurObj.getAttribute("highlighted")!="0") return false;
				oCurObj.setAttribute("active","1");
				var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
				if(oParams==null) return false;
				var oEmpty = document.getElementById(argobj.pid + "_EMPTYAREA");
				var oHighlighted = document.getElementById(argobj.pid + "_HIGHLIGHT");
				if(oEmpty==null || oHighlighted==null) return false;
				if(oEmpty.style.display!="none")
				{
					oEmpty.style.display = "none";
					oHighlighted.style.display = "block";
					if(oParams.selectSingleNode("blink").text=="yes")
					{
						var sDelay = oParams.selectSingleNode("timer").text;
						var nDelay = parseFloat(sDelay);
						if(isNaN(nDelay)) nDelay = 1;
						TransitionBlink({ targetid: argobj.pid, delay: nDelay });
					}
					oCurObj.setAttribute("highlighted","1");
				}
				return true;
			}

function HighlightOff(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.style.display=="none") return false;
				oCurObj.setAttribute("active","0");
				var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
				if(oParams==null) return false;
				var oEmpty = document.getElementById(argobj.pid + "_EMPTYAREA");
				var oHighlighted = document.getElementById(argobj.pid + "_HIGHLIGHT");
				if(oEmpty==null || oHighlighted==null) return false;
				if(oEmpty.style.display=="none")
				{
					oHighlighted.style.display = "none";
					oEmpty.style.display = "block";
					oCurObj.setAttribute("highlighted","0");
					if(oParams.selectSingleNode("blink").text=="yes") ClearTransitionBlink(argobj.pid);
				}
				return true;
			}

function Constructor(argobj)
                {
                    if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					var bGlobal = oParams.selectSingleNode("var_scope").text=="global";
					var sVarName = oParams.selectSingleNode("input_id").text=="" ? "input_var" : oParams.selectSingleNode("input_id").text;
					var oInput = document.getElementById(argobj.pid+"_input");
					if(bGlobal) {
						g_arVars[sVarName] = oInput.value;
					} else {
						g_arSlideVars[sVarName] = oInput.value;
					}
					return true;
				}

function ReadInput(argobj)
                {
                    if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					var bGlobal = oParams.selectSingleNode("var_scope").text=="global";
					var sVarName = oParams.selectSingleNode("input_id").text=="" ? "input_var" : oParams.selectSingleNode("input_id").text;
					var oInput = document.getElementById(argobj.pid+"_input");
					if(bGlobal) {
						g_arVars[sVarName] = oInput.value;
					} else {
						g_arSlideVars[sVarName] = oInput.value;
					}
					PlayObjectSound({ pid: argobj.pid, snd: "click" });
					return true;
				}

function AcceptInput(argobj)
                {
                    if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					var bGlobal = oParams.selectSingleNode("var_scope").text=="global";
					var sVarName = oParams.selectSingleNode("input_id").text=="" ? "input_var" : oParams.selectSingleNode("input_id").text;
					var oInput = document.getElementById(argobj.pid+"_input");
					if(bGlobal) {
						g_arVars[sVarName] = oInput.value;
					} else {
						g_arSlideVars[sVarName] = oInput.value;
					}
					FireEvent(argobj.pid, "onLeave");
					return true;
				}

function DisableInput(argobj)
                {
                    if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oInput = document.getElementById(argobj.pid+"_input");
					oInput.disabled = true;
					return true;
				}

function EnableInput(argobj)
                {
                    if(argobj.pid==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oInput = document.getElementById(argobj.pid+"_input");
					oInput.disabled = false;
					return true;
				}

function SetValue(argobj)
                {
                    if(argobj.pid==null || argobj.val==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oParams = g_oParams.selectSingleNode("./param[@objectid='"+argobj.pid+"']");
					if(oParams==null) return false;
					var bGlobal = oParams.selectSingleNode("var_scope").text=="global";
					var sVarName = oParams.selectSingleNode("input_id").text=="" ? "input_var" : oParams.selectSingleNode("input_id").text;
					var oInput = document.getElementById(argobj.pid+"_input");
					var sString = ReplacePropertiesInStr(argobj.val);
					sString = ReplaceVariablesInStr(sString);
					oInput.value = sString;
					if(bGlobal)
					{
						g_arVars[sVarName] = oInput.value;
					}
					else
					{
						g_arSlideVars[sVarName] = oInput.value;
					}
					return true;
				}

function GetProperty(argobj)
				{
					if(argobj.pid==null || argobj.property==null) return false;
					var oCurObj = document.getElementById(argobj.pid);
					if(oCurObj==null) return false;
					var oInput = document.getElementById(argobj.pid+"_input");
					switch(argobj.property.toLowerCase()) {
						case "disabled":		g_vPropertyValue = oInput.disabled ? "1" : "0";	break;
						case "value":			g_vPropertyValue = oInput.value; 	break;
						default: return true;
					}
					return true;
				}

function Constructor(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(g_aParams[argobj.pid]==null)
				{
					g_aParams[argobj.pid] = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
					if(g_aParams[argobj.pid]==null) return false;
				}
				CallMethod("005_pointer", "Adjust", { pid: argobj.pid });
				var oPointer = document.getElementById(argobj.pid + "_POINTER");
				if(oPointer!=null)
				{
					oPointer.style.visibility = "hidden";
					oPointer.style.display = "block";
					if(g_isMSIE)
					{
						if(oPointer.filters[0]!=null)
						{
							oPointer.filters[0].Apply();
							oPointer.style.visibility = "visible";
							oPointer.filters[0].Play();
						}
						else
						{
							oPointer.style.visibility = "visible";
						}
					}
					else
					{
						var sTransDur = g_aParams[argobj.pid].selectSingleNode("pointer_transition_duration").text;
						var nTransDur = parseFloat(sTransDur);
						if(isNaN(nTransDur)) nTransDur = 0.5;
						ProcessFFTranIn({ targetid: argobj.pid + "_POINTER", transtype: "12", transdur: nTransDur });
					}
				}
				return true;
			}

function Adjust(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(g_aParams[argobj.pid]==null) return false;
				var sPostfix = g_isMSIE ? "_W3C" : "_VML";
				var oFull = document.getElementById(argobj.pid + "_FULL" + sPostfix);
				var oShort = document.getElementById(argobj.pid + "_SHORT" + sPostfix);
				var oPointer = document.getElementById(argobj.pid + "_POINTER" + sPostfix);
				if(oFull!=null)
				{
					oFull.innerHTML = ".";
					oFull.style.display = "none";
				}
				if(oShort!=null)
				{
					oShort.innerHTML = ".";
					oShort.style.display = "none";
					sPostfix = g_isMSIE ? "_VML" : "_W3C";
					oShort = document.getElementById(argobj.pid + "_SHORT" + sPostfix);
					var oShortTxt = document.getElementById(argobj.pid + "_SHORT_TXT");
					if(oShort!=null && oShortTxt!=null)
					{
						var iH = oShortTxt.offsetHeight;
						var iHFull = oCurObj.offsetHeight;
						iH = iH + 24;
						oShort.style.height = iH + "px";
						if(g_isMSIE)
						{
							var nYUnit;
							var nXUnit;
							var iX0 = "0";
							var iX1;
							var iX2;
							var iX3 = "10000";
							var iY0 = "0";
							var iY1;
							var iY2;
							var iY3 = "10000";
							var sPath;
							var oShape = document.getElementById(argobj.pid + "_SHORT_shape");
							if(oShape!=null)
							{
								var iR = parseInt(oShape.getAttribute("radius"), 10);
								var iW = parseInt(oShape.style.width, 10);
								nXUnit = 10000/iW;
								nYUnit = 10000/iH;
								iX1 = Math.round(iR*nXUnit);
								iX2 = Math.round(10000 - iR*nXUnit);
								iY1 = Math.round(iR*nYUnit);
								iY2 = Math.round(10000 - iR*nYUnit);
								sPath = "M" + iX0 + "," + iY1 + " QY" +  iX1 + "," + iY0 + " L" + iX2 + "," + iY0 + " QX" + iX3 + "," + iY1 + " L" + iX3 + "," + iY2 + " QY" + iX2 + "," + iY3 + " L" + iX1 + "," + iY3 + " QX" + iX0 + "," + iY2 + " XE";
								oShape.style.height = iH + "px";
								oShape.path = sPath;
							}
						}
						var sPosition = g_aParams[argobj.pid].selectSingleNode("pointer_position").text;
						var iTop = 0;
						switch(sPosition)
						{
							case "ML":
							case "MR":
							{
								iTop = Math.floor(0.5*(iHFull - iH));
								break;
							}
							case "BL":
							case "BC":
							case "BR":
							{
								iTop = iHFull - iH;
								break;
							}
							default: break;
						}
						if(iTop<0) iTop = 0;
						oShort.style.top = iTop + "px";
					}
				}
				if(oPointer!=null)
				{
					oPointer.innerHTML = ".";
					oPointer.style.display = "none";
				}
				oPointer = document.getElementById(argobj.pid + "_POINTER");
				if(oCurObj.getAttribute("short--desc--display")!="1" && oCurObj.getAttribute("desc--display")!="1") oCurObj.style.clip = oPointer.getAttribute("clip-idle");
				return true;
			}

function Over(argobj)
			{
				if(argobj.pid==null || argobj.elem==null ) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false; 
				if(oCurObj.getAttribute("is--selected")=="1") return true;
				if(g_aParams[argobj.pid]==null) return false;
				var sDisplay = g_aParams[argobj.pid].selectSingleNode("display_method").text;
				if(sDisplay=="method") return false;
				var oPointer = argobj.elem;
				var oShape;
				var oFill;
				var oStyle;
				if(g_aParams[argobj.pid].selectSingleNode("sound.enabled").text=="yes") PlayObjectSound({ pid: argobj.pid, snd: "over" });
				if(g_isMSIE)
				{
					oShape = document.getElementById(argobj.pid + "_POINTER_shape");
					if(oShape!=null)
					{
						oShape.strokecolor = oPointer.getAttribute("strokecolor-over");
						oShape.strokeweight = oPointer.getAttribute("strokeweight-over");
						oFill = oShape.fill;
						if(oFill!=null)
						{
							oFill.color = oPointer.getAttribute("color-over");
							oFill.color2 = oPointer.getAttribute("color2-over");
							oFill.angle = oPointer.getAttribute("angle-over");
						}
					}
				}
				else
				{
					oShape = document.getElementById(argobj.pid + "_POINTER_W3C");
					if(oShape!=null)
					{
						oStyle = oShape.style;
						oStyle.borderColor = oPointer.getAttribute("strokecolor-over");
						oStyle.borderWidth = oPointer.getAttribute("strokeweight-over");
						oStyle.backgroundImage = oPointer.getAttribute(g_sCSSPrefix + "-gradient-over");
					}
				}
				var oTxt = document.getElementById(argobj.pid + "_POINTER_TXT");
				if(oTxt!=null)
				{
					oStyle = oTxt.style;
					oStyle.color = oPointer.getAttribute("fontcolor-over");
					oStyle.fontFamily = oPointer.getAttribute("fontfamily-over");
					oStyle.fontSize = oPointer.getAttribute("fontsize-over");
					oStyle.fontStyle = oPointer.getAttribute("fontstyle-over");
					oStyle.fontWeight = oPointer.getAttribute("fontweight-over");
				}
				switch(sDisplay)
				{
					case "over":
					{
						if(oCurObj.getAttribute("is--selected")!="1")
						{
							CallMethod("005_pointer", "DisplayDescription", { pid: argobj.pid, shape: oPointer });
						}
						break;
					}
					case "overclick":
					{
						if(oCurObj.getAttribute("short--desc--display")!="1")
						{
							var oShort = document.getElementById(argobj.pid+"_SHORT");
							if(oShort!=null) CallMethod("005_pointer", "DisplayShortDescription", { pid: argobj.pid, shape: oPointer });
						}
						break;
					}
				}
				return true;
			}

function Out(argobj)
			{
				if(argobj.pid==null || argobj.elem==null ) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false; 
				if(g_aParams[argobj.pid]==null) return false;
				var sDisplay = g_aParams[argobj.pid].selectSingleNode("display_method").text;
				if(sDisplay=="method") return false;
				if(oCurObj.getAttribute("is--selected")=="1" && (sDisplay!="over")) return true;
				var oPointer = argobj.elem;
				var oShape;
				var oFill;
				var oStyle;
				if(g_isMSIE)
				{
					oShape = document.getElementById(argobj.pid + "_POINTER_shape");
					if(oShape!=null)
					{
						oShape.strokecolor = oPointer.getAttribute("strokecolor-idle");
						oShape.strokeweight = oPointer.getAttribute("strokeweight-idle");
						oFill = oShape.fill;
						if(oFill!=null)
						{
							oFill.color = oPointer.getAttribute("color-idle");
							oFill.color2 = oPointer.getAttribute("color2-idle");
							oFill.angle = oPointer.getAttribute("angle-idle");
						}
					}
				}
				else
				{
					oShape = document.getElementById(argobj.pid + "_POINTER_W3C");
					if(oShape!=null)
					{
						oStyle = oShape.style;
						oStyle.borderColor = oPointer.getAttribute("strokecolor-idle");
						oStyle.borderWidth = oPointer.getAttribute("strokeweight-idle");
						oStyle.backgroundImage = oPointer.getAttribute(g_sCSSPrefix + "-gradient-idle");
					}
				}
				var oTxt = document.getElementById(argobj.pid + "_POINTER_TXT");
				if(oTxt!=null)
				{
					oStyle = oTxt.style;
					oStyle.color = oPointer.getAttribute("fontcolor-idle");
					oStyle.fontFamily = oPointer.getAttribute("fontfamily-idle");
					oStyle.fontSize = oPointer.getAttribute("fontsize-idle");
					oStyle.fontStyle = oPointer.getAttribute("fontstyle-idle");
					oStyle.fontWeight = oPointer.getAttribute("fontweight-idle");
				}
				switch(sDisplay)
				{
					case "over":
					{
						CallMethod("005_pointer", "HideDescription", { pid: argobj.pid, shape: oPointer });
						break;
					}
					case "overclick":
					{
						if(oCurObj.getAttribute("short--desc--display")=="1")
						{
							var oShort = document.getElementById(argobj.pid + "_SHORT");
							if(oShort!=null) CallMethod("005_pointer", "HideShortDescription", { pid: argobj.pid, shape: oPointer });
						}
						break;
					}
				}
				return true;
			}

function Click(argobj)
			{
				if(argobj.pid==null || argobj.elem==null ) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(g_aParams[argobj.pid]==null) return false;
				var sDisplay = g_aParams[argobj.pid].selectSingleNode("display_method").text;
				if(sDisplay=="over" || sDisplay=="method") return false;
				var oPointer = argobj.elem;
				var oShape;
				var oFill;
				var oStyle;
				if(g_aParams[argobj.pid].selectSingleNode("sound.enabled").text=="yes") PlayObjectSound({ pid: argobj.pid, snd: "click" });
				if(oCurObj.getAttribute("is--selected")=="1")
				{
					CallMethod("005_pointer", "HideDescription", { pid: argobj.pid, shape: oPointer });
					CallMethod("005_pointer", "Deselect", { pid: argobj.pid, shape: oPointer });
				}
				else
				{
					if(sDisplay=="overclick") CallMethod("005_pointer", "HideShortDescription", { pid: argobj.pid, shape: oPointer });
					CallMethod("005_pointer", "DisplayDescription", { pid: argobj.pid, shape: oPointer });
					CallMethod("005_pointer", "Select", { pid: argobj.pid, shape: oPointer });
				}
				return true;
			}

function Deselect(argobj)
			{
				if(argobj.pid==null || argobj.shape==null) return false;
