					oStyle.fontStyle = argobj.shape.getAttribute("fontstyle-idle");
					oStyle.fontWeight = argobj.shape.getAttribute("fontweight-idle");
				}
				oCurObj.setAttribute("is--selected","0");
				return true;
			}

