function DisplayShortDescription(argobj)
			{
				if(argobj.pid==null || argobj.shape==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(g_aParams[argobj.pid]==null) return false;
				var oShape = argobj.shape;
				var oShort = document.getElementById(argobj.pid + "_SHORT");
				if(oShort!=null)
				{
					oShort.style.display = "";
					oCurObj.style.clip = "rect(auto, auto, auto, auto)";
					if(g_isMSIE)
					{
						oShort.style.visibility = "hidden";
						oShort.style.display = "inline";
						if(oShort.filters[0]!=null)
						{
							oShort.filters[0].Apply();
							oShort.style.visibility = "visible";
							oShort.filters[0].Play();
						}
						else
						{
							oShort.style.visibility = "visible";
						}
					}
					else 
					{
						var sTransDur = g_aParams[argobj.pid].selectSingleNode("short_transition_duration").text;
						var nTransDur = parseFloat(sTransDur);
						if(isNaN(nTransDur)) nTransDur = 0.5;
						ProcessFFTranIn({ targetid: argobj.pid + "_SHORT", transtype: g_aParams[argobj.pid].selectSingleNode("short_transition_type").text, transdur: nTransDur });
					}
				}
				oCurObj.setAttribute("z--stored", oCurObj.style.zIndex);
				oCurObj.style.zIndex = 799;
				oCurObj.setAttribute("short--desc--display","1");
				if(g_aParams[argobj.pid].selectSingleNode("narration_short").text!="") PlaySound(oItem.selectSingleNode("narration_short").text, argobj.pid);
				return true;
			}

function HideShortDescription(argobj)
			{
				if(argobj.pid==null || argobj.shape==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				var oShort = document.getElementById(argobj.pid + "_SHORT");
				if(oShort!=null)
				{
					oShort.style.visibility = "hidden";
					oShort.style.display = "none";
					oCurObj.style.clip = argobj.shape.getAttribute("clip-idle");
				}
				var sZStored = oCurObj.getAttribute("z--stored");
				if(sZStored!=null) oCurObj.style.zIndex = parseInt(sZStored, 10);
				oCurObj.setAttribute("short--desc--display","");
				StopSound();
				return true;
			}

function DisplayDescription(argobj)
			{
				if(argobj.pid==null || argobj.shape==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(g_aParams[argobj.pid]==null) return false;

				var aObjects = g_oSlide.selectNodes("frames/frame/object[@type='005_pointer']");
				var sId = "";
				var oShape;
				var oObject;
				for(var i=0;i<aObjects.length;i++)
				{
					sId = aObjects[i].getAttribute("id");
					if(sId==argobj.pid) continue;
					oObject = document.getElementById(sId);
					if(oObject==null) continue;
					oShape = document.getElementById(sId + "_POINTER");
					if(oShape==null) continue;
					if(oObject.getAttribute("desc--display")=="1")
					{
						CallMethod("005_pointer", "HideDescription", { pid: sId, shape: oShape });
						CallMethod("005_pointer", "Deselect", { pid: sId, shape: oShape });
					}
				}
				//CallMethod("005_pointer", "Select", argobj);
				sId = argobj.pid + "_FULL";
				var oDesc = document.getElementById(sId);
				if(oDesc!=null)
				{
					oCurObj.setAttribute("z--stored", oCurObj.style.zIndex);
					oCurObj.style.zIndex = 799;
					oCurObj.style.clip = "rect(auto, auto, auto, auto)";
					oDesc.style.visibility = "hidden";
					oDesc.style.display = "";
					if(g_isMSIE)
					{
						if(oDesc.filters[0]!=null)
						{
							oDesc.filters[0].Apply();
							oDesc.style.visibility = "visible";
							oDesc.filters[0].Play();
						}
						else
						{
							oDesc.style.visibility = "visible";
						}
					}
					else
					{
						var sTransDur = g_aParams[argobj.pid].selectSingleNode("transition_duration").text;
						var nTransDur = parseFloat(sTransDur);
						if(isNaN(nTransDur)) nTransDur = 0.5;
						ProcessFFTranIn({ targetid: sId, transtype: g_aParams[argobj.pid].selectSingleNode("transition_type").text, transdur: nTransDur });
					}
				}
				oCurObj.setAttribute("desc--display","1");
				oCurObj.setAttribute("object--open","1");
				if(g_aParams[argobj.pid].selectSingleNode("narration_full").text!="") PlaySound(oItem.selectSingleNode("narration_full").text, argobj.pid);
				return true;
			}

function HideDescription(argobj)
			{
				if(argobj.pid==null || argobj.shape==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(oCurObj.getAttribute("desc--display")==null || oCurObj.getAttribute("desc--display")=="") return false;
				var sId = argobj.pid + "_FULL";
				var oDesc = document.getElementById(sId);
				if(oDesc!=null)
				{
					oCurObj.style.zIndex = oCurObj.getAttribute("z--stored");
					oDesc.style.display = "none";
					oCurObj.style.clip = argobj.shape.getAttribute("clip-idle");
				}
				oCurObj.setAttribute("desc--display", "");
				oCurObj.setAttribute("object--open","");
				StopSound();
				CallMethod("005_pointer", "Deselect", argobj);
				return true;
			}

function Open(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(g_aParams[argobj.pid]==null)
				{
					g_aParams[argobj.pid] = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
					if(g_aParams[argobj.pid]==null) return false;
				}
				var oPointer = document.getElementById(argobj.pid + "_POINTER");
				if(oPointer==null) return false;
				CallMethod("005_pointer", "DisplayDescription", { pid: argobj.pid, shape: oPointer });
				CallMethod("005_pointer", "Select", { pid: argobj.pid, shape: oPointer });
				return true;
			}

function Close(argobj)
			{
				if(argobj.pid==null) return false;
				var oCurObj = document.getElementById(argobj.pid);
				if(oCurObj==null) return false;
				if(g_aParams[argobj.pid]==null)
				{
					g_aParams[argobj.pid] = g_oParams.selectSingleNode("./param[@objectid='" + argobj.pid + "']");
					if(g_aParams[argobj.pid]==null) return false;
				}
				var oPointer = document.getElementById(argobj.pid + "_POINTER");
				if(oPointer==null) return false;
				CallMethod("005_pointer", "HideDescription", { pid: argobj.pid, shape: oPointer });
				return true;
			}

